import React, { Component } from 'react';
import AeroMap from "./components/AeroMap";
import MapSettings from "./components/MapSettings";
import { Segment } from "semantic-ui-react";
import "./css/style.css";

class MapArea extends Component {

    constructor(props){
        super(props);
        this.state = {
            mapLat: 49.258063,
            mapLong: -123.195911,
            targLat: 0,
            targLng: 0,
            followPlane: false,
            drawPlane: true,
            drawTarget: true,
            zoom: 10,
        };
        this.planeLat = 0;
        this.planeLong = 0;
    };

    componentWillUpdate(){

    };

    changeSettingsState = (state) => {
        this.setState(state);
    };

    zoomToPlane = () => {
        const { currentData } = this.props;
        if (!currentData || !currentData.flightData) return;
        const { latitude, longitude } = currentData.flightData;
        const eps = Math.random()*0.00000001;
        this.setState({ mapLat: latitude+eps, mapLong: longitude+eps, zoom: 19+eps });
    };

    setNewTarget = (inputLat, inputLng) => {
        var inLatHolder = inputLat;
        var inLngHolder = inputLng;
        this.setState({
            targLat: inLatHolder,
            targLng: inLngHolder,
        })
    }

    render(){
        const { height, currentData } = this.props;
        const { mapLat, mapLong, targLat, targLng, zoom, followPlane, drawPlane, drawTarget } = this.state;
        if (currentData && currentData.flightData) {
            this.planeLat = currentData.flightData.latitude;
            this.planeLong = currentData.flightData.longitude;
        }
        return (
            <Segment
                style={{
                    padding: "0px",
                    display: "flex",
                    borderLeft: "none",
                    height: "100%",
                    borderTopLeftRadius: 0,
                    borderBottomLeftRadius: 0,
                }}
            >

                <AeroMap
                    planeLat={this.planeLat}
                    planeLong={this.planeLong}
                    mapLat={mapLat}
                    mapLong={mapLong}
                    mapHeight={height - 42}
                    targLat={targLat}
                    targLng={targLng}
                    follow={followPlane}
                    zoom={zoom}
                    drawPlane={drawPlane}
                />

                <MapSettings
                    drawPlane={drawPlane}
                    drawTarget={drawTarget}
                    follow={followPlane}
                    zoomToPlane={this.zoomToPlane}
                    changeSettingsState={this.changeSettingsState}
                    setNewTarget={this.setNewTarget}
                />
            </Segment>
        );
    }

}

export default MapArea;
