import React, { Component } from 'react';
import { Segment, Header, Button, Icon } from "semantic-ui-react";
import "./style.css";

class DropArea extends Component {
    constructor(props) {
        super(props);
        this.state = {
            dropLine: "",
            testHeight: 0,
            dropOpen: true,
        };
    }

    toggleSettings = () => {
        this.setState((state) => { return { settingsOpen: !state.settingsOpen }; });
      };

    // Font should be 0.5" == 36 pt

    dropPayload = () => {
        const { currentData } = this.props;
        const { testHeight } = this.state;
        if (!currentData || !currentData.flightData){
            this.setState({ dropLine: "Error: Altitude not found!"});
            return;
        }
        this.setState({ dropLine: this.state.dropLine + "Water released at " + currentData.flightData.height + " m."});
        this.setState({ dropLine: this.state.dropLine + "Habitat released at " + currentData.flightData.height + " m."});
        // SEND SIGNAL THROUGH SERVER TO PHONE, PHONE SHOULD INTERPRET THIS INPUT AND TELL SERVO TO RELEASE WATER + HABITATS
        return;
    };


    releaseCDA = () => {
        const { currentData } = this.props;
        if (!currentData || !currentData.flightData){
            this.setState({ dropLine: "Error: Altitude not found!"});
            return;
        }
        this.setState({ dropLine: this.state.dropLine + "CDA released at " + currentData.flightData.height + " m."});
        return;
    };

    render(){
        const { currentData } = this.props;
        const { flightData } = currentData;
        const { dropLine } = this.state;
        return (
            <Segment
                style={{
                    height: "100%",
                    marginTop: "0px",
                    marginLeft: "1em",
                    minWidth: "300px",
                    boxSizing: "border-box",
                }}
            >
                <Header as="h4">Payload Release:</Header>
                <Segment>
                    <Header as="h5">Airdrop:</Header>
                    <div style={{ width: "100%" }}>
                        <div style={{ margin: "auto", width: "max-content" }}>
                            <Button basic color='brown' content="Cargo (Modules)" icon='first aid' basic onClick={this.dropPayload}/>
                            <br/>
                            <br/>
                            <Button basic color='violet' content="Colonists (CDAs)" icon='space shuttle' basic onClick={this.dropHabitat} />
                        </div>
                    </div> <br/>
                

            <div style={{ width: "100%", overflow: "hidden", fontSize: "12pt" }}>
                <table id="flight_data_table">
                    <tbody>
                        <tr>  
                            <td>
                                <Header>
                                    <Icon name='tint' size='tiny'/>Water: {dropLine}
                                </Header>
                            </td>
                        </tr>
                        <tr>  
                            <td>
                                <Header>
                                    <Icon name='home' size='tiny'/>Habitat:
                                </Header>
                            </td>
                        </tr>
                        <tr>  
                            <td>
                                <Header>
                                    <Icon name='users' size='tiny'/>CDA 1:
                                </Header>
                            </td>
                        </tr>
                        <tr>  
                            <td>
                                <Header>
                                    <Icon name='users' size='tiny'/>CDA 2:
                                </Header>
                            </td>
                        </tr>
                    </tbody>
                </table>
            </div>
            </Segment>
            </Segment>
        );
    }
}

export default DropArea;
