import React, { Component } from 'react';
import { Segment, Header, Button, Icon, Modal, Statistic, Card } from "semantic-ui-react";
import DomArea from "../DomArea";
import "./style.css";

class DropArea extends Component {
  state = {
    watAlt: 0,
    habAlt: 0,
    oneAlt: 0,
    twoAlt: 0,
    cargoDropped: false,
    oneDropped: false,
    twoDropped: false,
    confirmationOpen1: false,
    confirmationOpen2: false,
    confirmationOpen3: false,
    confirmationOpen4: false,
  };

  // Font should be 0.5" == 36 pt
  dropPayload = () => {
    const { currentData } = this.props;
    if (!currentData || !currentData.flightData || (currentData.flightData.altitude == null)) {
      this.setState({ confirmationOpen4: true });
      return;
    }
    if (!this.state.cargoDropped) {
      // SEND SIGNAL THROUGH SERVER TO PHONE, PHONE SHOULD INTERPRET THIS INPUT AND TELL SERVO TO RELEASE WATER + HABITATS
      this.setState({ watAlt: currentData.flightData.altitude });
      this.setState({ habAlt: currentData.flightData.altitude });
      this.setState({ cargoDropped: true });
      // send signal to phone to release
    } else if (this.state.cargoDropped) {
      this.setState({ confirmationOpen1: true });
    }
  };

  releaseCDAOne = () => {
    const { currentData } = this.props;
    if (!currentData || !currentData.flightData || (currentData.flightData.altitude == null)) {
      this.setState({ confirmationOpen4: true });
      return;
    }
    if (!this.state.oneDropped) {
      // SEND SIGNAL THROUGH SERVER TO PHONE, PHONE SHOULD INTERPRET THIS INPUT AND TELL SERVO TO RELEASE WATER + HABITATS
      this.setState({ oneAlt: currentData.flightData.altitude });
      this.setState({ oneDropped: true });
      // send signal to phone to release
    } else if (this.state.oneDropped && this.state.twoDropped) {
      this.setState({ confirmationOpen2: true });
    }
  };

  releaseCDATwo = () => {
    const { currentData } = this.props;
    if (!currentData || !currentData.flightData || (currentData.flightData.altitude == null)) {
      this.setState({ confirmationOpen4: true });
      return;
    }
    if (!this.state.twoDropped) {
      // SEND SIGNAL THROUGH SERVER TO PHONE, PHONE SHOULD INTERPRET THIS INPUT AND TELL SERVO TO RELEASE WATER + HABITATS
      this.setState({ twoAlt: currentData.flightData.altitude });
      this.setState({ twoDropped: true });
      // send signal to phone to release
