import React, { Component } from 'react';
import { Header, Checkbox, Segment, Button, Input, Popup, Message } from "semantic-ui-react";

class MapSettings extends Component {
    constructor(props){
        super(props);
        this.state = {
            settingsOpen: false,  
            showPopup: false,  
            mTargLat: 49.258063,
            mTargLng: -123.195911,
            mLatHolder: 49.258063,
            mLngHolder: -123.195911,
        }

        this.setTLAT = this.setTLAT.bind(this);
        this.setTLNG = this.setTLNG.bind(this);
        this.passTarget = this.passTarget.bind(this);
    }

    toggleSettings = ()  => {
        this.setState((state) => { return {settingsOpen: !state.settingsOpen}; });
    };

    setTLAT(event){
        if (90 < event.target.value || event.target.value < -90){
            //
        } else {
            this.setState({mLatHolder: event.target.value});
        }
    }

    setTLNG(event){
        if (180 < event.target.value || event.target.value < -180){
            //
        } else {
            this.setState({mLngHolder: event.target.value});
        }
    }

    openMarkup = () => {
        const { changeSettingsState, drawPlane } = this.props;
        return (
            <div style={{ padding: "1em", minWidth: "250px", minHeight:"100%", maxHeight: "100%" }}>
                <Header as="h4">Map Settings:</Header>
                <Segment style={{ display: "flex", flexDirection: "column"}}>
                    <Header as="h5">Display Settings</Header>
                    <Checkbox
                        toggle
                        label="Plane"
                        checked={drawPlane}
                        onChange={() => {changeSettingsState({ drawPlane: !drawPlane })}}
                    />
                    <Checkbox
                        toggle
                        label="Target"
                    />
                    <Checkbox
                        toggle
                        label="Drop prediction point"
                    />
                    <Checkbox
                        toggle
                        label="Wind direction"
                    />
                    <Checkbox
                        toggle
                        label="Heading"
                    />
                </Segment>
                <Segment style={{ display: "flex", flexDirection: "column"}}>
                    <Header as="h5">Movement</Header>
                    <Button
                        style={{ marginBottom: "0.5em"}}
                        basic
                        content="Zoom camera to plane"
                        onClick={this.props.zoomToPlane}
                    />
                    <Checkbox
                        toggle
                        label="Follow plane"
                    />
                </Segment>

                <Segment style={{ display: "flex", flexDirection: "column"}}>
                    <Header as="h5">Target</Header>
                    <p>Lattitude:   {this.state.mTargLat}</p>
                    <p>Longitude:   {this.state.mTargLng}</p>
                    <React.Fragment>
                    <Popup
                        trigger={<Button color='olive'>Input Target Location</Button>}
                        on='click'
                        position='bottom center'
                    >
                    <form onSubmit={this.handleSubmit}>
                        <label>
                        <Input
                            size='mini'
                            label={{ basic: true, content: '°N' }}
                            labelPosition='right'
                            placeholder='Lattitude...'
                            type="number"
                            value={this.state.mLatHolder}
                            onChange={this.setTLAT}
                        />  
                        <Input
                            size='mini'
                            label={{ basic: true, content: '°W' }}
                            labelPosition='right'
                            placeholder='Longitude...'
                            type="number"
                            value={this.state.mLngHolder}
                            onChange={this.setTLNG}
                        />
                        </label>
                        <Button
                            size="mini"
                            content="Submit"
                            color='olive'
                            onClick={this.passTarget}
                        />
                    </form>
                    </Popup>
                    </React.Fragment>
                </Segment>

                <Button
                    compact
                    size="mini"
                    icon="chevron left"
                    content="Hide Settings"
                    style={{
                        position: "absolute",
                        bottom: "1em",
                    }}
                    onClick={this.toggleSettings}
                />
            </div>
        );
    };

    passTarget = () => {
        this.setState({
            mTargLat: this.state.mLatHolder,
            mTargLng: this.state.mLngHolder,
        })

        var passedLat = this.state.mTargLat;
        var passedLng = this.state.mTargLng;
        this.props.callbackFromParent(passedLat, passedLng);
    }

    render(){
        if (this.state.settingsOpen)
            return this.openMarkup();
        else{
            return (
                <div style={{ position: "absolute", right: "1em", bottom: "1em" }}>
                    <Button
                        compact
                        size="mini"
                        icon="chevron right"
                        content="Show Settings"
                        onClick={this.toggleSettings}
                        style={{ width: "100px" }}
                    />
                </div>
            )
        }
        
    }
}

export default MapSettings;
