import React, { Component } from 'react';
import { Segment, Header, Button } from "semantic-ui-react";
import CommunicationArea from "../CommunicationArea";
import DataDisplay from "./components/DataDisplay";
import "./style.css";

class DataArea extends Component {
    constructor(props){
        super(props);
        
        this.state = {
            simulate: false,
            test: true,
            lat: 0,
            lng: 0,
            gAlt: 0,
            bAlt: 0,
            roc: 0,
            aSpeed: 0,
            gSpeed: 0,
            appTime: 0,
            location: "Not Calibrated",
            sStatus: "Not Implemented",
            packet: "Not Implemented",
        };

        this.simulateEntries = this.simulateEntries.bind(this);
        this.simulateProgression = this.simulateProgression.bind(this);
    }

    simulateEntries(){
        this.setState({
            lat: Number(Math.round(parseFloat(49.262475 + 'e' + 5)) + 'e-' + 5),
            lng: Number(Math.round(parseFloat(-123.249019 + 'e' + 5)) + 'e-' + 5),
            gAlt: Number(Math.round(parseFloat(60 + 'e' + 2)) + 'e-' + 2),
            bAlt: Number(Math.round(parseFloat(this.state.gAlt + Math.random() - Math.random() + 'e' + 2)) + 'e-' + 2),
            roc: Number(Math.round(parseFloat(0 + 'e' + 2)) + 'e-' + 2),
            gSpeed: Number(Math.round(parseFloat(15 + 'e' + 2)) + 'e-' + 2),
            aSpeed: Number(Math.round(parseFloat(this.state.gSpeed + Math.random()*6 - Math.random()*6 + 'e' + 2)) + 'e-' + 2),
            appTime: Number(Math.round(parseFloat(0 + 'e' + 4)) + 'e-' + 4),
            location: "Not Calibrated",
            sStatus: "Not Implemented",
            packet: "Not Implemented",
        });
    }

    simulateProgression(){
        var split = Math.random() - Math.random();
        this.setState({
            roc: Number(Math.round(parseFloat(Math.random()*0.08 - Math.random()*0.08 + 'e' + 2)) + 'e-' + 2),
            gAlt: Number(Math.round(parseFloat(this.state.gAlt + this.state.roc + 'e' + 2)) + 'e-' + 2),
            bAlt: Number(Math.round(parseFloat(this.state.gAlt + Math.random() - Math.random() + 'e' + 2)) + 'e-' + 2),
            gSpeed: Number(Math.round(parseFloat(15 + Math.random()*0.08 - Math.random()*0.08 + 'e' + 2)) + 'e-' + 2),
            aSpeed: Number(Math.round(parseFloat(this.state.gSpeed + Math.random()*0.02 - Math.random()*0.02 + 'e' + 2)) + 'e-' + 2),
            lat: Number(Math.round(parseFloat((split*this.state.gSpeed/1000)/111111 + this.state.lat + 'e' + 5)) + 'e-' + 5),
            lng: Number(Math.round(parseFloat((split*this.state.gSpeed/1000)/111111 + this.state.lng + 'e' + 5)) + 'e-' + 5),
            appTime: Number(Math.round(parseFloat(this.state.appTime + (1/100) + 'e' + 4)) + 'e-' + 4)
        });
    }   

    render(){
        const { recieveData, currentData } = this.props;
        return (
            <Segment
                style={{
                    height: "100%",
                    marginTop: "0px",
                    marginLeft: "1em",
                    minWidth: "350px",
                    boxSizing: "border-box",
                }}
            >
                <Header as="h4">Data:</Header>
                <CommunicationArea recieveData={recieveData}/>
                <Segment>
                    <Header as="h5">Live Flight Data:</Header>
                    <DataDisplay currentData={currentData} />
                    <p>Lattitude:           {this.state.lat}</p>
                    <p>Longitude:           {this.state.lng}</p>
                    <p>GPS Altitude:        {this.state.gAlt}</p>
                    <p>Barometric Altitude: {this.state.bAlt}</p>
                    <p>Rate of Climb:       {this.state.roc}</p>
                    <p>Air Speed:           {this.state.aSpeed}</p>
                    <p>Ground Speed:        {this.state.gSpeed}</p>
                    <p>Application Time:    {this.state.appTime}</p>
                    <p>Map Location:        {this.state.location}</p>
                    <p>Server Status:       {this.state.sStatus}</p>
                    <p>Last Packet:         {this.state.packet}</p>
                </Segment>
                <Button
                    basic
                    compact
                    size="mini"
                    basic color='blue'
                    content="Simulate Data"
                    style={{
                        position: "absolute",
                        bottom: "1em",
                    }}
                    onClick={this.simulateEntries}
                />

                <Button
                    basic
                    compact
                    size="mini"
                    basic color='green'
                    content="Run Simulation"
                    attached='left'
                    style={{
                        position: "absolute",
                        bottom: "1em",
                        right: "10em"
                    }}
                    onClick={() => {
                        if (!this.state.simulate){
                        this.refresher = setInterval(() => this.simulateProgression(), 10)   // the compiler says this is "assigned but never used" --> ignore that, it does stuff. Do not remove this line!
                        this.state.simulate = true;
                    }
                    }
                    }
                />

                <Button
                    basic
                    compact
                    size="mini"
                    basic color='red'
                    content="Stop Simulation"
                    attached='right'
                    style={{
                        position: "absolute",
                        bottom: "1em",
                        right: "1em"
                    }}
                    onClick={() => {
                        if (this.state.simulate){
                        clearInterval(this.refresher);
                        this.state.simulate = false;
                    }
                    }
                    }
                />

            </Segment>
        );
    }
}

export default DataArea;
