import React, { Component } from 'react';
import AeroMap from "./components/AeroMap";
import MapSettings from "./components/MapSettings";
import { Segment } from "semantic-ui-react";
import "./css/style.css";

class MapArea extends Component {

    constructor(props){
        super(props);
        this.state = {
            mapLat: 49.258063,
            mapLong: -123.195911,
            targLat: null,
            targLng: null,
            followPlane: true,
            drawPlane: true,
            drawTarget: true,
            drawPredictionPoint: true,
            zoom: 10,
        };
        this.planeLat = 0;
        this.planeLong = 0;
        this.payloadLat = 0;
        this.payloadLong = 0;
        this.cdaLat = 0;
        this.cdaLong = 0;
    };

    componentWillUpdate(){

    };

    changeSettingsState = (state) => {
        this.setState(state);
    };

    zoomToPlane = () => {
        const { currentData } = this.props;
        if (!currentData || !currentData.flightData) return;
        const { latitude, longitude } = currentData.flightData;
        const eps = Math.random()*0.00000001;
        this.setState({ mapLat: latitude+eps, mapLong: longitude+eps, zoom: 19+eps });
    };

    zoomToTarget = () => {
        if (this.state.targLat == null || this.state.targLng == null) return;
        const eps = Math.random()*0.00000001;
        this.setState({ mapLat: this.state.targLat + eps, mapLong: this.state.targLng + eps, zoom: 19+eps });
    };

    setNewTarget = (inputLat, inputLng) => {
        this.setState({
            targLat: inputLat,
            targLng: inputLng,
        })
    }

    render(){
        const { height, currentData } = this.props;
        const { mapLat, mapLong, targLat, targLng, zoom, followPlane, drawPlane, drawTarget, drawPredictionPoint} = this.state;
        if (currentData && currentData.flightData) {
            this.planeLat = currentData.flightData.latitude;
            this.planeLong = currentData.flightData.longitude;
            this.payloadLat = currentData.flightData.payloadLatLong[0];
            this.payloadLong = currentData.flightData.payloadLatLong[1];
            this.cdaLat = currentData.flightData.cdaLatLong[0];
            this.cdaLong = currentData.flightData.cdaLatLong[1];

        }
        if (!this.state.followPlane){
            if((this.state.mapLat != this.planeLat) || (this.state.mapLong != this.planeLong)){
                this.setState({ mapLat: this.planeLat, mapLong: this.planeLong });
            }
        }

        return (
            <Segment
                style={{
                    padding: "0px",
                    display: "flex",
                    borderLeft: "none",
                    height: "100%",
                    borderTopLeftRadius: 0,
                    borderBottomLeftRadius: 0,
                }}
            >

                <AeroMap
                    planeLat={this.planeLat}
                    planeLong={this.planeLong}
                    mapLat={mapLat}
                    mapLong={mapLong}
                    mapHeight={height - 42}
                    targLat={targLat}
                    targLng={targLng}
                    zoom={zoom}
                    drawPlane={drawPlane}
                    drawTarget={drawTarget}
                    drawPredictionPoint={drawPredictionPoint}
                    payloadLat = {this.payloadLat}
                    payloadLong = {this.payloadLong}
                    cdaLat = {this.cdaLat}
                    cdaLong = {this.cdaLong}
                />

                <MapSettings
                    drawPlane={drawPlane}
                    drawTarget={drawTarget}
                    drawPredictionPoint={drawPredictionPoint}
                    followPlane={followPlane}
                    zoomToPlane={this.zoomToPlane}
                    zoomToTarget={this.zoomToTarget}
                    changeSettingsState={this.changeSettingsState}
                    setNewTarget={this.setNewTarget}
                />
            </Segment>
        );
    }

}

export default MapArea;
