import React, { Component } from 'react';
import { Tab } from "semantic-ui-react";
import "../MapArea/css/resize.css";
import MapArea from "../MapArea";
import pbDataArea from "./Playback/pbDataArea";
import TodoArea from "../TodoArea";

class Playback extends Component {

    dataLog = [];

    state = {
        currentData: {},
    }

    lastPacketTime = 0;

    render(){
        const { currentData } = this.state;
        const height = window.innerHeight - 90;
        return (
            <Tab.Pane
                style={{
                    paddingLeft: 0,
                    paddingBottom: "3em",
                    border: "none",
                    height: height,
                    boxSizing: "content-box",
                    paddingRight: 0,
                    maxWidth: "100%",
                }}>
                <div style={{ display: "flex", height: "calc(100% - 3em)", overflow: "hidden", overflowX: "auto", paddingBottom: "1em" }}>
                    <MapArea currentData={currentData} height={height} />
                    <pbDataArea saveLog={this.saveDataLog} clearLog={this.clearDataLog} recieveData={this.recieveData} currentData={currentData} />

                </div>
            </Tab.Pane>
        );
    }
}

export default Playback;
