import React, { Component } from 'react';
import { Segment, Header, Button, Icon, Modal, Statistic } from "semantic-ui-react";
import DomArea from "../DomArea";
import "./style.css";

class DropArea extends Component {
  state = {
    watAlt: 0,
    habAlt: 0,
    oneAlt: 0,
    twoAlt: 0,
    cargoDropped: false,
    oneDropped: false,
    twoDropped: false,
    confirmationOpen1: false,
    confirmationOpen2: false,
    confirmationOpen3: false,
  };

  // Font should be 0.5" == 36 pt
  dropPayload = () => {
    const { currentData } = this.props;
    if (!currentData || !currentData.flightData) {
      this.setState({ waterLine: -1 });
      this.setState({ habitatLine: -1 });
      return;
    }
    if (!this.state.cargoDropped) {
      // SEND SIGNAL THROUGH SERVER TO PHONE, PHONE SHOULD INTERPRET THIS INPUT AND TELL SERVO TO RELEASE WATER + HABITATS
      this.setState({ waterLine: currentData.flightData.altitude });
      this.setState({ habitatLine: currentData.flightData.altitude });
      this.setState({ cargoDropped: true });
      // send signal to phone to release
    } else if (this.state.cargoDropped) {
      this.setState({ confirmationOpen1: true });
    }
  };

  releaseCDAOne = () => {
    const { currentData } = this.props;
    if (!currentData || !currentData.flightData) {
      this.setState({ cdaOne: -1 });
      return;
    }
    if (!this.state.oneDropped) {
      // SEND SIGNAL THROUGH SERVER TO PHONE, PHONE SHOULD INTERPRET THIS INPUT AND TELL SERVO TO RELEASE WATER + HABITATS
      this.setState({ cdaOne: currentData.flightData.altitude });
      this.setState({ oneDropped: true });
      // send signal to phone to release
    } else if (this.state.oneDropped && this.state.twoDropped) {
      this.setState({ confirmationOpen2: true });
    }
  };

  releaseCDATwo = () => {
    const { currentData } = this.props;
    if (!currentData || !currentData.flightData) {
      this.setState({ cdaTwo: -1 });
      return;
    }
    if (!this.state.twoDropped) {
      // SEND SIGNAL THROUGH SERVER TO PHONE, PHONE SHOULD INTERPRET THIS INPUT AND TELL SERVO TO RELEASE WATER + HABITATS
      this.setState({ cdaTwo: currentData.flightData.altitude });
      this.setState({ twoDropped: true });
      // send signal to phone to release
    } else if (this.state.oneDropped && this.state.twoDropped) {
      this.setState({ confirmationOpen2: true });
    }
  };

  resetDrops = () => {
    this.setState({ confirmationOpen3: true });
  };

  render() {
    const { watAlt, habAlt, oneAlt, twoAlt, confirmationOpen1, confirmationOpen2, confirmationOpen3 } = this.state;
    return (
      <DomArea header="Payload Release">
        <Segment padded='very'>
          <Header as="h4">Airdrop:</Header>
          {' '}
          <div style={{ width: "100%", overflow: "hidden", fontSize: "12pt" }}>
            <table id="flight_data_table">
              <tbody>
                <tr>
                  <td>
                    <div>
                      <Header
                        as="h3"
                        icon="tint"
                        color="blue"
                        content={(
                          <div>
                            <span>
                              Water &emsp; &emsp;
                            </span>
                            <Button basic color="blue" content="Drop" icon="tint" onClick={this.dropPayload} />
                          </div>
                      )}
                      />
                    </div>
                  </td>
                </tr>
                <tr>
                  <td>
                    <div>
                      <Header
                        as="h1"
                        color="black"
                        content={(
                          <div>
                            <span>
                              &emsp; &ensp;
                              {' '}
                              <Statistic horizontal label="ft." value={watAlt} size='huge' />
                            </span>
                          </div>
                          )}
                      />
                    </div>
                  </td>
                </tr>
                <tr>
                  <td>
                    <div>
                      <Header
                        as="h3"
                        icon="home"
                        color="brown"
                        content={(
                          <div>
                            <span>
                              Habitat &emsp; &ensp;
                            </span>
                            <Button basic color="brown" content="Drop" icon="first aid" onClick={this.dropPayload} />
                          </div>
                        )}
                      />
                    </div>
                  </td>
                </tr>
                <tr>
                  <td>
                    <div>
                      <Header
                        as="h1"
                        color="black"
                        content={(
                          <div>
                            <span>
                              &emsp; &ensp;
                              {' '}
                              <Statistic horizontal label="ft." value={habAlt} size='huge' />
                            </span>
                          </div>
                          )}
                      />
                    </div>
                  </td>
                </tr>
                <tr>
                  <td>
                    <div>
                      <Header
                        as="h3"
                        icon="paper plane"
                        color="violet"
                        content={(
                          <div>
                            <span>
                              CDA 1 &emsp; &emsp;
                            </span>
                            <Button basic color="violet" content="Drop" icon="space shuttle" onClick={this.releaseCDAOne} />
                          </div>
                          )}
                      />
                    </div>
                  </td>
                </tr>
                <tr>
                  <td>
                    <div>
                      <Header
                        as="h1"
                        color="black"
                        content={(
                          <div>
                            <span>
                              &emsp; &ensp;
                              {' '}
                              <Statistic horizontal label="ft." value={oneAlt} size='huge' />
                            </span>
                          </div>
                          )}
                      />
                    </div>
                  </td>
                </tr>
                <tr>
                  <td>
                    <div>
                      <Header
                        as="h3"
                        icon="paper plane"
                        color="violet"
                        content={(
                          <div>
                            <span>
                              CDA 2 &emsp; &emsp;
                            </span>
                            <Button basic color="violet" content="Drop" icon="space shuttle" onClick={this.releaseCDATwo} />
                          </div>
                          )}
                      />
                    </div>
                  </td>
                </tr>
                <tr>
                  <td>
                    <div>
                      <Header
                        as="h1"
                        color="black"
                        content={(
                          <div>
                            <span>
                              &emsp; &ensp;
                              {' '}
                              <Statistic horizontal label="ft." value={twoAlt} size='huge' />
                            </span>
                          </div>
                          )}
                      />
                    </div>
                  </td>
                </tr>
              </tbody>
            </table>
          </div>
          <br />
          <div style={{ margin: "auto", width: "max-content" }}>
            <Button basic color="red" content="Reset Drops" icon="repeat" onClick={this.resetDrops} />
          </div>
          <Modal
            open={confirmationOpen1}
          >
            <Modal.Header>
                Notice: Cargo
            </Modal.Header>
            <Modal.Content>
              <div style={{ padding: "1em" }}>
                <Icon name="exclamation" size="big" color="yellow" />
                  Both the water module and the habitat module have already been released.
              </div>
            </Modal.Content>
            <Modal.Actions>
              <Button
                content="OK"
                color="green"
                onClick={() => { this.setState({ confirmationOpen1: false }); }}
              />
              <Button
                negative
                content="Reset Payload Release"
                onClick={() => {
                  this.setState({
                    confirmationOpen1: false,
                    cargoDropped: false,
                    watAlt: 0,
                    habAlt: 0,
                  });
                }}
              />
            </Modal.Actions>
          </Modal>
          <Modal
            open={confirmationOpen2}
          >
            <Modal.Header>
                Notice: Gliders
            </Modal.Header>
            <Modal.Content>
              <div style={{ padding: "1em" }}>
                <Icon name="exclamation" size="big" color="yellow" />
                  Both of the CDAs have already been released.
              </div>
            </Modal.Content>
            <Modal.Actions>
              <Button
                content="OK"
                color="green"
                onClick={() => { this.setState({ confirmationOpen2: false }); }}
              />
              <Button
                negative
                content="Reset CDA Release"
                onClick={() => {
                  this.setState({
                    confirmationOpen2: false,
                    oneDropped: false,
                    twoDropped: false,
                    oneAlt: 0,
                    twoAlt: 0,
                  });
                }}
              />
            </Modal.Actions>
          </Modal>
          <Modal
            open={confirmationOpen3}
          >
            <Modal.Header>
                Alert: Reset Payload + CDA Drop
            </Modal.Header>
            <Modal.Content>
              <div style={{ padding: "1em" }}>
                <Icon name="exclamation" size="big" color="red" />
                  Are you sure you want to clear all of the current drop values? This action cannot be undone.
              </div>
            </Modal.Content>
            <Modal.Actions>
              <Button
                negative
                content="Reset Drops"
                onClick={() => {
                  this.setState({
                    confirmationOpen3: false,
                    oneDropped: false,
                    twoDropped: false,
                    cargoDropped: false,
                    watAlt: 0,
                    habAlt: 0,
                    oneAlt: 0,
                    twoAlt: 0,
                  });
                }}
              />
              <Button
                content="Cancel"
                onClick={() => { this.setState({ confirmationOpen3: false }); }}
              />
            </Modal.Actions>
          </Modal>
        </Segment>
      </DomArea>
    );
  }
}

export default DropArea;
