import React, { Component } from 'react';
import { Segment, Header, Button, Icon, Modal, Statistic, Card } from "semantic-ui-react";
import DomArea from "../DomArea";
import "./style.css";

class DropArea extends Component {
  state = {
    watAlt: 0,
    habAlt: 0,
    oneAlt: 0,
    twoAlt: 0,
    confirmationOpen1: false,
    confirmationOpen2: false,
    confirmationOpen3: false,
    confirmationOpen4: false,
  };

  // Font should be 0.5" == 36 pt
  dropPayload = () => {
    const { currentData, webSocketManager, isConnected, cargoDropped, setDropped } = this.props;
    if (!isConnected) {
      this.setState({ confirmationOpen4: true });
      return;
    }
    if (!cargoDropped && currentData) {
      // SEND SIGNAL THROUGH SERVER TO PHONE, PHONE SHOULD INTERPRET THIS INPUT AND TELL SERVO TO RELEASE WATER + HABITATS
      webSocketManager.release("habitat + water");
      this.setState({ watAlt: currentData.altitude });
      this.setState({ habAlt: currentData.altitude });
      setDropped("cargoDropped", true);
      // send signal to phone to release
    } else if (cargoDropped) {
      this.setState({ confirmationOpen1: true });
    }
  };

  releaseCDAOne = () => {
    const { currentData, webSocketManager, isConnected, oneDropped, twoDropped, setDropped } = this.props;
    if (!isConnected) {
      this.setState({ confirmationOpen4: true });
      return;
    }
    if (!oneDropped && currentData) {
      // SEND SIGNAL THROUGH SERVER TO PHONE, PHONE SHOULD INTERPRET THIS INPUT AND TELL SERVO TO RELEASE CDA 1
      webSocketManager.release("CDA One");
      this.setState({ oneAlt: currentData.altitude });
      setDropped("oneDropped", true);
      // send signal to phone to release
    } else if (oneDropped && twoDropped) {
      this.setState({ confirmationOpen2: true });
    }
  };

  releaseCDATwo = () => {
    const { currentData, webSocketManager, isConnected, oneDropped, twoDropped, setDropped } = this.props;
    if (!isConnected) {
      this.setState({ confirmationOpen4: true });
      return;
    }
    if (!twoDropped && currentData && currentData) {
      // SEND SIGNAL THROUGH SERVER TO PHONE, PHONE SHOULD INTERPRET THIS INPUT AND TELL SERVO TO RELEASE CDA 2
      webSocketManager.release("CDA Two");
      this.setState({ twoAlt: currentData.altitude });
      setDropped("twoDropped", true);
      // send signal to phone to release
    } else if (oneDropped && twoDropped) {
      this.setState({ confirmationOpen2: true });
    }
  };

  resetDrops = () => {
    this.setState({ confirmationOpen3: true });
  };

  // <Button basic color="brown" content="Drop" icon="first aid" onClick={this.dropPayload} />

  render() {
    const { watAlt, habAlt, oneAlt, twoAlt, confirmationOpen1, confirmationOpen2, confirmationOpen3, confirmationOpen4 } = this.state;
    const { setDropped } = this.props;
    return (
      <DomArea header="Payload Release">
        <Segment>
          <Header as="h4">
              Airdrop: &emsp; &emsp; &emsp; &emsp; &emsp;
            <Button basic color="red" content="Reset Drops" icon="repeat" compact="true" onClick={this.resetDrops} />
          </Header>
          <div style={{ width: "100%", overflow: "hidden", fontSize: "12pt" }}>
            <Card>
              <Card.Content>
                <Header
                  as="h3"
                  icon="tint"
                  color="blue"
                  content="Water"
                  floated="left"

                />
                <Header
                  as="h3"
                  icon="home"
                  color="brown"
                  content="Habitat"
                  floated="right"
                />
                <Header
                  color="black"
                  floated="left"
                  content={(
                    <span>
                        &emsp; &ensp;
                      <Statistic horizontal label="ft." value={watAlt} size="large" />
                    </span>
                    )}
                />
                <Header
                  color="black"
                  floated="right"
                  content={(
                    <span>
                        &emsp; &ensp;
                      <Statistic horizontal label="ft." value={habAlt} size="large" />
                    </span>
                    )}
                />
              </Card.Content>
              <Button
                basic
                color="black"
                compact="true"
                onClick={this.dropPayload}
                content={(
                  <span>
                    <Icon fitted name="tint" color="blue" />
                        &nbsp; + &nbsp;
                    <Icon
                      fitted
                      name="home"
                      color="brown"
                    />
                        &nbsp; Drop
                  </span>
                  )}
              />
            </Card>
            <Card>
              <Card.Content>
                <Header
                  as="h3"
                  icon="paper plane"
                  color="violet"
                  content="CDA 1"
                  floated="left"
                />
                <Header
                  as="h3"
                  icon="paper plane"
                  color="violet"
                  content="CDA 2"
                  floated="right"
                />
                <Header
                  color="black"
                  floated="left"
                  content={(
                    <span>
                        &emsp; &ensp;
                      <Statistic horizontal label="ft." value={oneAlt} size="large" />
                    </span>
                        )}
                />
                <Header
                  color="black"
                  floated="right"
                  content={(
                    <span>
                        &emsp; &ensp;
                      <Statistic horizontal label="ft." value={twoAlt} size="large" />
                    </span>
                    )}
                />
              </Card.Content>
              <Button.Group>
                <Button basic color="violet" content="Drop 1" icon="space shuttle" onClick={this.releaseCDAOne} />
                <Button.Or />
                <Button basic color="violet" content="Drop 2" icon="space shuttle" onClick={this.releaseCDATwo} />
              </Button.Group>
            </Card>
          </div>
          <Modal
            open={confirmationOpen1}
          >
            <Modal.Header>
                Notice: Cargo
            </Modal.Header>
            <Modal.Content>
              <div style={{ padding: "1em" }}>
                <Icon name="exclamation" size="big" color="yellow" />
                  Both the water module and the habitat module have already been released.
              </div>
            </Modal.Content>
            <Modal.Actions>
              <Button
                content="OK"
                color="green"
                onClick={() => { this.setState({ confirmationOpen1: false }); }}
              />
              <Button
                negative
                content="Reset Payload Release"
                onClick={() => {
                  this.setState({
                    confirmationOpen1: false,
                    watAlt: 0,
                    habAlt: 0,
                  });
                  setDropped("cargoDropped", false);
                }}
              />
            </Modal.Actions>
          </Modal>
          <Modal
            open={confirmationOpen2}
          >
            <Modal.Header>
                Notice: Gliders
            </Modal.Header>
            <Modal.Content>
              <div style={{ padding: "1em" }}>
                <Icon name="exclamation" size="big" color="yellow" />
                  Both of the CDAs have already been released.
              </div>
            </Modal.Content>
            <Modal.Actions>
              <Button
                content="OK"
                color="green"
                onClick={() => { this.setState({ confirmationOpen2: false }); }}
              />
              <Button
                negative
                content="Reset CDA Release"
                onClick={() => {
                  this.setState({
                    confirmationOpen2: false,
                    oneAlt: 0,
                    twoAlt: 0,
                  });
                  setDropped("oneDropped", false);
                  setDropped("twoDropped", false);
                }}
              />
            </Modal.Actions>
          </Modal>
          <Modal
            open={confirmationOpen3}
          >
            <Modal.Header>
                Alert: Reset Payload + CDA Drop
            </Modal.Header>
            <Modal.Content>
              <div style={{ padding: "1em" }}>
                <Icon name="exclamation" size="big" color="red" />
                  Are you sure you want to clear all of the current drop values? This action cannot be undone.
              </div>
            </Modal.Content>
            <Modal.Actions>
              <Button
                negative
                content="Reset Drops"
                onClick={() => {
                  this.setState({
                    confirmationOpen3: false,
                    watAlt: 0,
                    habAlt: 0,
                    oneAlt: 0,
                    twoAlt: 0,
                  });
                  setDropped("oneDropped", false);
                  setDropped("twoDropped", false);
                  setDropped("cargoDropped", false);
                }}
              />
              <Button
                content="Cancel"
                onClick={() => { this.setState({ confirmationOpen3: false }); }}
              />
            </Modal.Actions>
          </Modal>
          <Modal
            open={confirmationOpen4}
          >
            <Modal.Header>
                Error: No Connection
            </Modal.Header>
            <Modal.Content>
              <div style={{ padding: "1em" }}>
                <Icon name="exclamation" size="big" color="red" />
                  Groundstation encountered an error communicating with the aircraft. Please check connection to the server.
              </div>
            </Modal.Content>
            <Modal.Actions>
              <Button
                content="Dismiss"
                onClick={() => {
                  this.setState({
                    confirmationOpen4: false,
                  });
                }}
              />
            </Modal.Actions>
          </Modal>
        </Segment>
      </DomArea>
    );
  }
}

export default DropArea;
