import React, { Component } from 'react';
import "../MapArea/css/resize.css";
import MapArea from "../MapArea";
import DataArea from "../DataArea";
import DropArea from "../DropArea";
import WebSocketManager from "../../helpers/WebSocketManager";
import { predictCDA, predictPayload } from "../../helpers/predictions";
import { getDirection } from "../../helpers/direction";
import { predictLoc } from "../../helpers/location";


class GroundStation extends Component {
    state = {
      currentData: {},
      isConnected: false,
      cargoDropped: false,
      oneDropped: false,
      twoDropped: false,
    };

    dataLog = [];

    planePositionHistory = [];

    setDropped = (drop, value) => {
      const change = {};
      change[drop] = value;
      this.setState(change);
    };

    setData = (rawData) => {
      const data = JSON.parse(rawData);
      const time = +Date.now();
      const timeElapsed = time - this.state.lastPacketTime;
      const { currentData, cargoDropped, oneDropped, twoDropped } = this.state;
      data.timeElapsed = timeElapsed;
      this.state.lastPacketTime = time;
      if (data.flightData) {
        data.flightData.timeRecieved = (time / 1000) || 0;

        data.flightData.payloadPredictionDistance = predictPayload(data.flightData.pitch, data.flightData.altitude, data.flightData.velocity, data.flightData.headwind);
        data.flightData.CDAPredictionDistance = predictCDA(data.flightData.pitch, data.flightData.altitude, data.flightData.velocity, data.flightData.headwind);

        data.flightData.direction = getDirection(currentData.flightData, data.flightData);

        if (data.flightData.direction === 0) {
          data.flightData.direction = this.state.oldDirection;
        }

        this.state.oldDirection = data.flightData.direction;

        data.flightData.payloadLatLong = predictLoc(data.flightData, data.flightData.payloadPredictionDistance);
        data.flightData.cdaLatLong = predictLoc(data.flightData, data.flightData.CDAPredictionDistance);

        data.flightData.cargoDropped = cargoDropped;
        data.flightData.oneDropped = oneDropped;
        data.flightData.twoDropped = twoDropped;

        this.planePositionHistory.push([data.flightData.latitude, data.flightData.longitude]);
        this.dataLog.push(data.flightData);
        this.setState({ currentData: data });
      }
    }

    setConnected = (isConnected) => {
      this.setState({ isConnected });
    }

    getConnected = () => {
      return this.state.isConnected;
    }

    webSocketManager = new WebSocketManager(this.setData, this.setConnected, this.getConnected);

    clearDataLog = () => {
      this.dataLog = [];
    };

    render() {
      const { currentData, isConnected, cargoDropped, oneDropped, twoDropped } = this.state;
      return (
        <div
          style={{
            paddingLeft: 0,
            paddingBottom: "1em",
            paddingTop: "1em",
            border: "none",
            height: "100%",
            boxSizing: "content-box",
            paddingRight: 0,
            maxWidth: "100%",
          }}
        >
          <div style={{ display: "flex", height: "calc(100% - 2em)", overflow: "hidden", overflowX: "auto", paddingBottom: "1em" }}>
            <MapArea currentData={currentData} planePositionHistory={this.planePositionHistory} />
            <DataArea
              dataLog={this.dataLog}
              saveLog={this.saveDataLog}
              clearLog={this.clearDataLog}
              receiveData={this.receiveData}
              currentData={currentData}
              webSocketManager={this.webSocketManager}
              isConnected={isConnected}
            />
            <DropArea
              currentData={currentData}
              webSocketManager={this.webSocketManager}
              isConnected={isConnected}
              cargoDropped={cargoDropped}
              oneDropped={oneDropped}
              twoDropped={twoDropped}
              setDropped={this.setDropped}
            />
          </div>
        </div>
      );
    }
}

export default GroundStation;
