import React, { Component } from 'react';
import { Segment, Header, Button } from "semantic-ui-react";
import CommunicationArea from "../CommunicationArea";
import DataDisplay from "./components/DataDisplay";
import "./style.css";

class DataArea extends Component {
    constructor(props){
        super(props);
    }

    render(){
        const { recieveData, currentData, saveLog, clearLog } = this.props;
        return (
            <Segment
                style={{
                    height: "100%",
                    marginTop: "0px",
                    marginLeft: "1em",
                    minWidth: "350px",
                    boxSizing: "border-box",
                }}
            >
                <Header as="h4">Data:</Header>
                <CommunicationArea recieveData={recieveData} />
                <Segment>
                    <Header as="h5">Live Flight Data:</Header>
                    <DataDisplay currentData={currentData} />
                    <p>Lattitude:           {this.state.lat}</p>
                    <p>Longitude:           {this.state.lng}</p>
                    <p>GPS Altitude:        {this.state.gAlt}</p>
                    <p>Barometric Altitude: {this.state.bAlt}</p>
                    <p>Rate of Climb:       {this.state.roc}</p>
                    <p>Air Speed:           {this.state.aSpeed}</p>
                    <p>Ground Speed:        {this.state.gSpeed}</p>
                    <p>Application Time:    {this.state.appTime}</p>
                    <p>Map Location:        {this.state.location}</p>
                    <p>Server Status:       {this.state.sStatus}</p>
                    <p>Last Packet:         {this.state.packet}</p>
                </Segment>
                <Segment>
                    <Header as="h5">Log Data:</Header>
                    <div style={{ width: "100%" }}>
                        <div style={{ margin: "auto", width: "max-content" }}>
                            <Button content="Export Log" basic onClick={saveLog} />
                            <Button content="Clear Log" basic onClick={clearLog} />
                        </div>
                    </div>
                </Segment>
            </Segment>
        );
    }
}

export default DataArea;
