export const findDirection = (oldData, newData) => {
  if (oldData == null || oldData.flightData == null) return [0,0];
  const dLat = newData.flightData.latitude - oldData.flightData.latitude;
  const dLong = newData.flightData.longitude - oldData.flightData.longitude;
  const mag = Math.sqrt(dLat**2 + dLong**2);
  if (mag == 0) return[0,0];
  return [dLat / mag, dLong / mag];
};
