import { predictCDA, predictPayload } from "./predictions";
import { getDirection } from "./direction";
import { predictLoc } from "./location";

const localServer = "ws://localhost:8888";
const remoteServer = "ws://104.248.73.89:8888";// ?????
const server = remoteServer;

class WebSocketManager {
  constructor() {
    this.connect();
  }

    state = {
      connected: false,
      lastPacketTime: null,
      currentData: null,
    };

    componentDidMount() {
      this.connect();
    }

    connect = () => {
      console.info("Connecting....");
      const connection = new WebSocket(server);

      connection.onopen = () => {
        this.setState({ connected: true });
        console.info("Connected to server");
      };

      connection.onerror = (error) => {
        console.error(
          "Socket encountered error: ",
          error.message,
          "Closing socket",
        );
        connection.close();
        console.info("Connection error", error);
      };
      connection.onmessage = (e) => {
        this.receiveData(e.data);
      };
      connection.onclose = () => {
        console.info("Disconnected from server");
      };
    };

  receiveData = (rawData) => {
    const { currentData } = this.state;
    const data = JSON.parse(rawData);
    const time = +Date.now();
    const timeElapsed = time - this.state.lastPacketTime;
    data.timeElapsed = timeElapsed;
    this.state.lastPacketTime = time;
    if (data.flightData) {
      data.flightData.timeRecieved = (time / 1000) || 0;

      data.flightData.payloadPredictionDistance = predictPayload(data.flightData.altitude, data.flightData.velocity, data.flightData.headwind);
      data.flightData.CDAPredictionDistance = predictCDA(data.flightData.altitude, data.flightData.velocity, data.flightData.headwind);

      data.flightData.direction = getDirection(currentData.flightData, data.flightData);

      data.flightData.payloadLatLong = predictLoc(data.flightData, data.flightData.payloadPredictionDistance);
      data.flightData.cdaLatLong = predictLoc(data.flightData, data.flightData.CDAPredictionDistance);

      this.planePositionHistory.push([data.flightData.latitude, data.flightData.longitude]);
      this.dataLog.push(data.flightData);

      this.setState({ currentData: data });
    }
  };

    testData = () => {
      const { connected } = this.state;
      if (!connected) {
        console.warn("Cannot send. Not connected");
        return;
      }
      const data = JSON.stringify(
        {
          type: "data",
          flightData: {
            latitude: 49.258063+Math.random()*0.00111,
            longitude: -123.195911+Math.random()*0.00111,
            altitude: 45.3,
            velocity: 10.3,
            headwind: -0.9,
          },
        },
      );
      console.info("Sending test data:", data);
      this.connection.send(data);
    };

    ping = () => {
      const { connected } = this.state;
      if (!connected) {
        console.warn("Cannot ping. Not connected");
        return;
      }
      const message = JSON.stringify(
        {
          type: "action",
          action: {
            // ping
          },
        },
      );
      console.info("Pinging:", message);
      this.connection.send(message);
    };

  release = (identity) => {
    const { connected } = this.state;
    if (!connected) {
      console.warn("Cannot drop. Not connected");
      return;
    }
    const message = JSON.stringify(
      {
        type: "action",
        action: {
          // drop details
          object: identity,
        },
      },
    );
    console.info("Sending drop signal:", message);
    this.connection.send(message);
  };

  /*
    check = () => {
      const { connection } = this.state;
      if (!connection || connection.readyState == WebSocket.CLOSED) this.connect();
    };
    */
}
export default WebSocketManager;
