import React, { Component } from 'react';
import { Segment, Header, Button, Modal, Icon } from "semantic-ui-react";
import CommunicationArea from "../CommunicationArea";
import DataDisplay from "./components/DataDisplay";
import "./style.css";

class DataArea extends Component {
    state = {
      confirmationOpen: false,
    };

    render() {
      const { recieveData, currentData, saveLog, clearLog } = this.props;
      const { confirmationOpen } = this.state;
      return (
        <Segment
          style={{
            height: "100%",
            marginTop: "0px",
            marginLeft: "1em",
            minWidth: "350px",
            boxSizing: "border-box",
          }}
        >
          <Header as="h4">Data:</Header>
          <CommunicationArea recieveData={recieveData} />
          <Segment>
            <Header as="h5" style={{ marginBottom: "0px" }}>Live Flight Data:</Header>
            <DataDisplay currentData={currentData} />
          </Segment>
          <Segment>
            <Header as="h5">Log Data:</Header>
            <div style={{ width: "100%" }}>
              <div style={{ margin: "auto", width: "max-content" }}>
                <Button content="Export Log" icon="save outline" basic onClick={saveLog} />
                <Button content="Clear Log" icon="cancel" negative basic onClick={() => { this.setState({ confirmationOpen: true }); }} />
              </div>
            </div>
          </Segment>
          <Modal
            open={confirmationOpen}
          >
            <Modal.Header>
                  Are you sure you want to clear the flight log?
            </Modal.Header>
            <Modal.Content>
              <div style={{ padding: "1em" }}>
                <Icon name="exclamation" size="big" color="red" />
                    This action cannot be undone.
              </div>
            </Modal.Content>
            <Modal.Actions>
              <Button
                content="Cancel"
                onClick={() => { this.setState({ confirmationOpen: false }); }}
              />
              <Button
                negative
                content="Clear Log"
                onClick={() => { this.setState({ confirmationOpen: false }); clearLog(); }}
              />
            </Modal.Actions>
          </Modal>
        </Segment>
      );
    }
}

export default DataArea;
