import React, { Component } from 'react';
import { Segment, Header, Button, Icon } from "semantic-ui-react";
import "./style.css";

class DropArea extends Component {
  constructor(props) {
    super(props);
    this.state = {
      waterLine: "Water module on aircraft.",
      habitatLine: "Habitat module on aircraft.",
      cdaOne: "CDA 1 attached to aircraft.",
      cdaTwo: "CDA 2 attached to aircraft.",
      dropOpen: true,
      cargoDropped: false,
      oneDropped: false,
      twoDropped: false,
    };
  }

    toggleSettings = () => {
      this.setState((state) => { return { dropOpen: !state.dropOpen }; });
    };

    // Font should be 0.5" == 36 pt

    dropPayload = () => {
      const { currentData } = this.props;
      if (!currentData || !currentData.flightData) {
        this.setState({ waterLine: " Error: Altitude not found!" });
        this.setState({ habitatLine: " Error: Altitude not found!" });
        return;
      }
      if (!this.state.cargoDropped) {
        // SEND SIGNAL THROUGH SERVER TO PHONE, PHONE SHOULD INTERPRET THIS INPUT AND TELL SERVO TO RELEASE WATER + HABITATS
        this.setState({ waterLine: ` Water released at ${currentData.flightData.altitude} m.` });
        this.setState({ habitatLine: ` Habitat released at ${currentData.flightData.altitude} m.` });
        this.setState({ cargoDropped: true });
        // send signal to phone to release
      } else if (this.state.cargoDropped) {
        window.alert("Payload has already been dropped!");
      }
    };

    releaseCDA = () => {
      const { currentData } = this.props;
      if (!currentData || !currentData.flightData) {
        this.setState({ cdaOne: " Error: Altitude not found!" });
        this.setState({ cdaTwo: " Error: Altitude not found!" });
        return;
      }
      if (!this.state.oneDropped) {
        // SEND SIGNAL THROUGH SERVER TO PHONE, PHONE SHOULD INTERPRET THIS INPUT AND TELL SERVO TO RELEASE WATER + HABITATS
        this.setState({ cdaOne: ` CDA 1 released at ${currentData.flightData.altitude} m.` });
        this.setState({ oneDropped: true });
        // send signal to phone to release
      } else if (!this.state.twoDropped) {
        // SEND SIGNAL THROUGH SERVER TO PHONE, PHONE SHOULD INTERPRET THIS INPUT AND TELL SERVO TO RELEASE WATER + HABITATS
        this.setState({ cdaTwo: ` CDA 2 released at ${currentData.flightData.altitude} m.` });
        this.setState({ twoDropped: true });
        // send signal to phone to release
      } else if (this.state.oneDropped && this.state.twoDropped) {
        window.alert("Both CDAs have already been dropped!");
      }
    };

    resetDrops = () => {
      if (window.confirm("Are you sure you want to reset all drop values?")) {
        this.setState({
          cargoDropped: false,
          oneDropped: false,
          twoDropped: false,
          waterLine: "Water module on aircraft.",
          habitatLine: "Habitat module on aircraft.",
          cdaOne: "CDA 1 attached to aircraft.",
          cdaTwo: "CDA 2 attached to aircraft.",
        });
      } else {
        //
      }
    }

  openMarkup = () => {
    const { waterLine, habitatLine, cdaOne, cdaTwo } = this.state;
    return (
        <Segment
            style={{
              height: "100%",
              marginTop: "0px",
              marginLeft: "1em",
              minWidth: "300px",
              boxSizing: "border-box",
            }}
        >
          <Header as="h4">Payload Release:</Header>
          <Segment>
            <Header as="h4">Airdrop:</Header>
            <div style={{ width: "100%" }}>
              <div style={{ margin: "auto", width: "max-content" }}>
                <Button basic color="brown" content="Cargo (Modules)" icon="first aid" onClick={this.dropPayload} />

                <Button basic color="violet" content="Colonists (CDAs)" icon="space shuttle" onClick={this.releaseCDA} />
              </div>
            </div>
            {' '}
            <br />


            <div style={{ width: "100%", overflow: "hidden", fontSize: "12pt" }}>
              <table id="flight_data_table">
                <tbody>
                <tr>
                  <td>
                    <Header as='h2' icon='tint' color='blue' content={waterLine} />
                  </td>
                </tr>
                <tr>
                  <td>
                    <Header as='h2' icon='home' color='brown' content={habitatLine}/>
                  </td>
                </tr>
                <tr>
                  <td>
                    <Header as='h2' icon='paper plane' color='yellow' content={cdaOne}/>
                  </td>
                </tr>
                <tr>
                  <td>
                    <Header as='h2' icon='paper plane' color='yellow' content={cdaTwo}/>
                  </td>
                </tr>
                </tbody>
              </table>
            </div>
            <br/>
            <div style={{ margin: "auto", width: "max-content" }}>
              <Button basic color="red" content="Reset Drops" icon="repeat" onClick={this.resetDrops} />
            </div>

          </Segment>
          <Button
              compact
              size="mini"
              icon="chevron left"
              content="Hide Drop"
              style={{
                position: "absolute",
                bottom: "4em",
              }}
              onClick={this.toggleSettings}
          />
        </Segment>
    );
  }

    render() {
      if (this.state.settingsOpen) {
        return this.openMarkup();
      } else {
        return (
            <div style={{ position: "absolute", right: "1em", bottom: "4em" }}>
              <Button
                  compact
                  size="mini"
                  icon="chevron right"
                  content="Show Settings"
                  onClick={this.toggleSettings}
                  style={{ width: "100px" }}
              />
            </div>
        );
      }
    }
}

export default DropArea;
