import React, { Component } from 'react';
import { Tab } from "semantic-ui-react";
import "../MapArea/css/resize.css";
import MapArea from "../MapArea";
import DataArea from "../DataArea";
import DropArea from "../DropArea";

import { predictPayload, predictCDA } from "../../helpers/predictions";
import { predictLoc } from "../../helpers/location";
import { getDirection } from "../../helpers/direction";


class GroundStation extends Component {
  dataLog = [];

  planePositionHistory = [];

  state = {
    currentData: {},
  };

  lastPacketTime = 0;

  timeout = 250;

  constructor(props) {
    super(props);
    this.state = {
      connection: null,
    };
    this.connect();
  }

  receivedData = (data) => {
    console.info("received:", data);
    this.receiveData(data);
  };

  componentDidMount() {
    this.connect();
  }

  connect = () => {
    console.info("Connecting....");
    const localServer = "ws://localhost:8888";
    const remoteServer = "ws://104.248.73.89:8888";
    const server = remoteServer;
    const connection = new WebSocket(server);
    this.connection = connection;
    const that = this; // cache the this
    connection.onopen = () => {
      this.setState({connected: true});
      this.setState({connection: connection});
      that.timeout = 250; // reset timer to 250 on open of websocket connection
      clearTimeout(connectInterval); // clear Interval on on open of websocket connection
      console.info("Connected to server");
    };
    connection.onerror = (error) => {
      console.error(
          "Socket encountered error: ",
          error.message,
          "Closing socket",
      );
      connection.close();
      console.info("Connection error", error);
    };
    connection.onmessage = (e) => {
      this.receivedData(e.data);
    };
    connection.onclose = () => {
      this.setState({connected: false});
      console.info("Disconnected from server");
      that.timeout += that.timeout; // increment retry interval
      connectInterval = setTimeout(this.check, Math.min(10000, that.timeout)); // call check function after timeout
    };
    let connectInterval;
  }

  check = () => {
    const { connection } = this.state;
    if (!connection || connection.readyState == WebSocket.CLOSED) this.connect(); // check if websocket instance is closed, if so call `connect` function.
  };

  // ==================================================================================

  receiveData = (rawData) => {
    const { currentData } = this.state;
    const data = JSON.parse(rawData);
    const time = +Date.now();
    const timeElapsed = time - this.lastPacketTime;
    data.timeElapsed = timeElapsed;
    this.lastPacketTime = time;
    if (data.flightData) {
      data.flightData.timeRecieved = (time / 1000) || 0;

      data.flightData.payloadPredictionDistance = predictPayload(data.flightData.altitude, data.flightData.velocity, data.flightData.headwind);
      data.flightData.CDAPredictionDistance = predictCDA(data.flightData.altitude, data.flightData.velocity, data.flightData.headwind);

      data.flightData.direction = getDirection(currentData.flightData, data.flightData);

      data.flightData.payloadLatLong = predictLoc(data.flightData, data.flightData.payloadPredictionDistance);
      data.flightData.cdaLatLong = predictLoc(data.flightData, data.flightData.CDAPredictionDistance);

      this.planePositionHistory.push([data.flightData.latitude, data.flightData.longitude]);
      this.dataLog.push(data.flightData);

      this.setState({ currentData: data });
    }
  };


  clearDataLog = () => {
    this.dataLog = [];
  };

  download = (filename, text) => {
    const pom = document.createElement('a');
    pom.setAttribute('href', `data:text/plain;charset=utf-8,${encodeURIComponent(text)}`);
    pom.setAttribute('download', filename);

    if (document.createEvent) {
      const event = document.createEvent('MouseEvents');
      event.initEvent('click', true, true);
      pom.dispatchEvent(event);
    } else {
      pom.click();
    }
  };

  saveDataLog = () => {
    console.info("Saving", this.dataLog.length, "records");
    let csv = "TimeSent, TimeRecieved, Latitude, Longitude, GPS Altitude (ft), Velocity (m/s), GPS Accuracy,"
      + "Payload Prediction (ft), Payload Target Lat, Payload Target Long, CDA Prediction (ft), CDA Target Lat, CDA Target Long";
    this.dataLog.forEach((d) => {
      csv = `${csv}\n${d.timeSent}, ${d.timeRecieved}, ${d.latitude}, ${d.longitude}, ${d.altitude}, ${d.velocity}, ${d.accuracy}, \
            ${d.payloadPredictionDistance},${d.payloadLatLong[0]}, ${d.payloadLatLong[1]},${d.CDAPredictionDistance},${d.cdaLatLong[0]}, ${d.cdaLatLong[1]}`;
    });

    this.download(`Flight Log ${new Date().toISOString()}.csv`, csv);
  };


  render() {
    const { currentData } = this.state;
    return (
      <Tab.Pane
        style={{
          paddingLeft: 0,
          paddingBottom: "1em",
          border: "none",
          height: "100%",
          boxSizing: "content-box",
          paddingRight: 0,
          maxWidth: "100%",
        }}
      >
        <div style={{ display: "flex", height: "calc(100% - 2em)", overflow: "hidden", overflowX: "auto", paddingBottom: "1em" }}>
          <MapArea currentData={currentData} planePositionHistory={this.planePositionHistory} />
          <DataArea saveLog={this.saveDataLog} clearLog={this.clearDataLog} receiveData={this.receiveData} currentData={currentData} />
          <DropArea currentData={currentData} />
        </div>
      </Tab.Pane>
    );
  }
}

export default GroundStation;
