import React, { Component } from 'react';
import { Segment, Header, Button } from "semantic-ui-react";
import "./style.css";

class DropArea extends Component {
    constructor(props) {
        super(props);
        this.state = {
            dropLine: "",
            altitude: null,
        }
    }

    dropPayload = () => {
        const { dropAlt } = this.props.altitude;
        if (!dropAlt){
            this.setState({ dropLine: "Error: Altitude not found!"});
            return;
        }
        this.setState({ dropLine: this.state.dropLine + "Water released at " + dropAlt + " m."});
        this.setState({ dropLine: this.state.dropLine + "Habitat released at " + dropAlt + " m."});
        // SEND SIGNAL THROUGH SERVER TO PHONE, PHONE SHOULD INTERPRET THIS INPUT AND TELL SERVO TO RELEASE WATER + HABITATS
        return;
    };


    releaseCDA = () => {
        const { dropAlt } = this.props.altitude;
        if (!dropAlt){
            this.setState({ dropLine: "Error: Altitude not found!"});
            return;
        }
        this.setState({ dropLine: this.state.dropLine + "CDA released at " + dropAlt + " m."});
        return;
    };

    render(){
        const {altitude} = this.props;
        return (
            <Segment
                style={{
                    height: "100%",
                    marginTop: "0px",
                    marginLeft: "1em",
                    minWidth: "380px",
                    boxSizing: "border-box",
                }}
            >
                <Header as="h4">Payload Release:</Header>
                <Segment>
                    <Header as="h5">Airdrop:</Header>
                    <div style={{ width: "100%" }}>
                        <div style={{ margin: "auto", width: "max-content" }}>
                            <Button basic color='brown' content="Cargo (Modules)" icon='warehouse' basic onClick={this.dropPayload} />
                            <Button basic color='violet' content="Colonists (CDAs)" icon='space shuttle' basic onClick={this.dropHabitat} />
                            <td><br/>{this.state.dropLine}</td>
                        </div>
                    </div>
                </Segment>
            </Segment>
        );
    }
}

export default DropArea;
