import React, { Component } from 'react';
import { Segment, Header, Button, Icon, Modal } from "semantic-ui-react";
import "./style.css";

class DropArea extends Component {
  state = {
    waterLine: "Water module on aircraft.",
    habitatLine: "Habitat module on aircraft.",
    cdaOne: "CDA 1 attached to aircraft.",
    cdaTwo: "CDA 2 attached to aircraft.",
    dropOpen: true,
    cargoDropped: false,
    oneDropped: false,
    twoDropped: false,
    confirmationOpen1: false,
    confirmationOpen2: false,
    confirmationOpen3: false,
  }

  toggleSettings = () => {
    this.setState((state) => { return { dropOpen: !state.dropOpen }; });
  };

  // Font should be 0.5" == 36 pt

  dropPayload = () => {
    const { currentData } = this.props;
    if (!currentData || !currentData.flightData) {
      this.setState({ waterLine: " Error: Altitude not found!" });
      this.setState({ habitatLine: " Error: Altitude not found!" });
      return;
    }
    if (!this.state.cargoDropped) {
      // SEND SIGNAL THROUGH SERVER TO PHONE, PHONE SHOULD INTERPRET THIS INPUT AND TELL SERVO TO RELEASE WATER + HABITATS
      this.setState({ waterLine: ` Water released at ${currentData.flightData.altitude} ft.` });
      this.setState({ habitatLine: ` Habitat released at ${currentData.flightData.altitude} ft.` });
      this.setState({ cargoDropped: true });
      // send signal to phone to release
    } else if (this.state.cargoDropped) {
      this.setState({ confirmationOpen1: true });
    }
  };

  releaseCDA = () => {
    const { currentData } = this.props;
    if (!currentData || !currentData.flightData) {
      this.setState({ cdaOne: " Error: Altitude not found!" });
      this.setState({ cdaTwo: " Error: Altitude not found!" });
      return;
    }
    if (!this.state.oneDropped) {
      // SEND SIGNAL THROUGH SERVER TO PHONE, PHONE SHOULD INTERPRET THIS INPUT AND TELL SERVO TO RELEASE WATER + HABITATS
      this.setState({ cdaOne: ` CDA 1 released at ${currentData.flightData.altitude} ft.` });
      this.setState({ oneDropped: true });
      // send signal to phone to release
    } else if (!this.state.twoDropped) {
      // SEND SIGNAL THROUGH SERVER TO PHONE, PHONE SHOULD INTERPRET THIS INPUT AND TELL SERVO TO RELEASE WATER + HABITATS
      this.setState({ cdaTwo: ` CDA 2 released at ${currentData.flightData.altitude} ft.` });
      this.setState({ twoDropped: true });
      // send signal to phone to release
    } else if (this.state.oneDropped && this.state.twoDropped) {
      this.setState({ confirmationOpen2: true });
    }
  };

  resetDrops = () => {
    this.setState({ confirmationOpen3: true });
  }

  openMarkup = () => {
    const { waterLine, habitatLine, cdaOne, cdaTwo, confirmationOpen1, confirmationOpen2, confirmationOpen3 } = this.state;
    return (
        <Segment
            style={{
              height: "100%",
              marginTop: "0px",
              marginLeft: "1em",
              minWidth: "450px",
              boxSizing: "border-box",
            }}
        >
          <Header as="h4">Payload Release:</Header>
          <Segment>
            <Header as="h4">Airdrop:</Header>
            <div style={{ width: "100%" }}>
              <div style={{ margin: "auto", width: "max-content" }}>
                <Button basic color="brown" content="Drop Modules" icon="first aid" onClick={this.dropPayload} />

                <Button basic color="violet" content="Drop CDAs" icon="space shuttle" onClick={this.releaseCDA} />
              </div>
            </div>
            {' '}
            <br />


            <div style={{ width: "100%", overflow: "hidden", fontSize: "12pt" }}>
              <table id="flight_data_table">
                <tbody>
                <tr>
                  <td>
                    <Header as="h2" icon="tint" color="blue" content={waterLine} />
                  </td>
                </tr>
                <tr>
                  <td>
                    <Header as="h2" icon="home" color="brown" content={habitatLine} />
                  </td>
                </tr>
                <tr>
                  <td>
                    <Header as="h2" icon="paper plane" color="yellow" content={cdaOne} />
                  </td>
                </tr>
                <tr>
                  <td>
                    <Header as="h2" icon="paper plane" color="yellow" content={cdaTwo} />
                  </td>
                </tr>
                </tbody>
              </table>
            </div>
            <br />
            <div style={{ margin: "auto", width: "max-content" }}>
              <Button basic color="red" content="Reset Drops" icon="repeat" onClick={this.resetDrops} />
            </div>
            <Modal
                open={confirmationOpen1}
            >
              <Modal.Header>
                Notice: Cargo
              </Modal.Header>
              <Modal.Content>
                <div style={{ padding: "1em" }}>
                  <Icon name="exclamation" size="big" color="yellow" />
                  Both the water module and the habitat module have already been released.
                </div>
              </Modal.Content>
              <Modal.Actions>
                <Button
                    content="OK"
                    color="green"
                    onClick={() => { this.setState({ confirmationOpen1: false }); }}
                />
                <Button
                    negative
                    content="Reset Payload Release"
                    onClick={() => {
                      this.setState({
                        confirmationOpen1: false,
                        cargoDropped: false,
                        waterLine: "Water module on aircraft.",
                        habitatLine: "Habitat module on aircraft.",
                      });
                    }}
                />
              </Modal.Actions>
            </Modal>

            <Modal
                open={confirmationOpen2}
            >
              <Modal.Header>
                Notice: Gliders
              </Modal.Header>
              <Modal.Content>
                <div style={{ padding: "1em" }}>
                  <Icon name="exclamation" size="big" color="yellow" />
                  Both of the CDAs have already been released.
                </div>
              </Modal.Content>
              <Modal.Actions>
                <Button
                    content="OK"
                    color="green"
                    onClick={() => { this.setState({ confirmationOpen2: false }); }}
                />
                <Button
                    negative
                    content="Reset CDA Release"
                    onClick={() => {
                      this.setState({
                        confirmationOpen2: false,
                        oneDropped: false,
                        twoDropped: false,
                        cdaOne: "CDA 1 attached to aircraft.",
                        cdaTwo: "CDA 2 attached to aircraft.",
                      });
                    }}
                />
              </Modal.Actions>
            </Modal>

            <Modal
                open={confirmationOpen3}
            >
              <Modal.Header>
                Alert: Reset Payload + CDA Drop
              </Modal.Header>
              <Modal.Content>
                <div style={{ padding: "1em" }}>
                  <Icon name="exclamation" size="big" color="red" />
                  Are you sure you want to clear all of the current drop values? This action cannot be undone.
                </div>
              </Modal.Content>
              <Modal.Actions>
                <Button
                    negative
                    content="Reset Drops"
                    onClick={() => {
                      this.setState({
                        confirmationOpen3: false,
                        oneDropped: false,
                        twoDropped: false,
                        cargoDropped: false,
                        waterLine: "Water module on aircraft.",
                        habitatLine: "Habitat module on aircraft.",
                        cdaOne: "CDA 1 attached to aircraft.",
                        cdaTwo: "CDA 2 attached to aircraft.",
                      });
                    }}
                />
                <Button
                    content="Cancel"
                    onClick={() => { this.setState({ confirmationOpen3: false }); }}
                />
              </Modal.Actions>
            </Modal>

          </Segment>
          <Button
              compact
              size="mini"
              icon="chevron left"
              content="Hide Drop"
              style={{
                position: "absolute",
                bottom: "2em",
              }}
              onClick={this.toggleSettings}
          />
        </Segment>
    );
  }

  render() {
    const { dropOpen } = this.state;
    if (dropOpen) {
      return this.openMarkup();
    } else {
      return (
          <Segment basic size="mini">
            <div style={{ position: "absolute", right: "3em", bottom: "2em" }}>
              <Button
                  compact
                  size="mini"
                  icon="chevron right"
                  content="Show Drop"
                  onClick={this.toggleSettings}
                  style={{ width: "100px" }}
              />
            </div>
          </Segment>
      );
    }
  }
}

export default DropArea;
