import React, { Component } from 'react';
import { Segment, Header, Icon, Button } from "semantic-ui-react";

const localServer = "ws://localhost:8888";
const remoteServer = "ws://104.248.73.89:8888";//?????
const server = remoteServer;

class CommunicationArea extends Component {

    constructor(props){
        super(props);
        this.connect()
    };


    state = {
        connected: false,
    };

    recievedData = (data) => {
        console.log("Recieved:", data);
        this.props.recieveData(data);
    };

    connect = () => {
        console.log("Connecting....");
        const connection = new WebSocket(server);
        connection.onopen = () => {
            this.setState({ connected: true });
            console.log("Connected to server");
        };
        connection.onerror = (error) => {
            console.log("Connection error",error);
        };
        connection.onmessage = (e) => {
            this.recievedData(e.data);
        };
        connection.onclose = () => {
            this.setState({ connected: false });
            console.log("Disconnected from server");
        };
        this.connection = connection;
    };

    sendData = () => {
        const { connected } = this.state;
        if (!connected){
            console.warn("Cannot send. Not connected");
            return;
        }
        //const data = JSON.stringify({type: "data", flightData: { latitude: 49.258063+Math.random()*0.00111, longitude: -123.195911+Math.random()*0.00111, altitude: 45.3, velocity: 10.3}});
        const data = JSON.stringify({type: "data", flightData: { latitude: 49.258063+Math.abs(Math.random()*0.00111), longitude: -123.195911+Math.abs(Math.random()*0.00111), altitude: 45.3, velocity: 10.3}});
        console.log("Sending:", data);
        this.connection.send(data);
    };

    render(){
        const { connected } = this.state;
        return (
            <Segment>
                <Header as="h5" style={{ marginBottom: "0px" }}>Communication:</Header>
                <div>
                    Connection status:&nbsp;
                    {connected ? (
                            <strong style={{ color: "limegreen" }}>Connected</strong>
                        ): (
                            <span>
                                <strong style={{ color: "firebrick" }}>Not Connected</strong>
                                &nbsp;
                                <Icon link name="sync" onClick={this.connect} />
                            </span>

                        )}
                </div>
                <Button compact basic onClick={this.sendData}>Send  Test Data</Button>
            </Segment>
        );
    }
}

export default CommunicationArea;
