import React, { Component } from 'react';
import { Segment, Header, Button } from "semantic-ui-react";
import CommunicationArea from "../CommunicationArea";
import DataDisplay from "./components/DataDisplay";
import "./style.css";

class DataArea extends Component {
    constructor(props) {
        //super(props);
        this.state = {
            dropLine: ""
        }
    }

    dropWater = () => {
        this.setState({ dropLine: this.state.dropLine + "\nWater released at " + "[altitude here]"});
    };

    dropHabitat = () => {
        this.setState({ dropLine: this.state.dropLine + "\nWater released at " + "[altitude here]"});
    };

    render(){
        const { recieveData, currentData, saveLog, clearLog } = this.props;
        return (
            <Segment
                style={{
                    height: "100%",
                    marginTop: "0px",
                    marginLeft: "1em",
                    minWidth: "350px",
                    boxSizing: "border-box",
                }}
            >
                <Header as="h4">Data:</Header>
                <CommunicationArea recieveData={recieveData}/>
                <Segment>
                    <Header as="h5">Live Flight Data:</Header>
                    <DataDisplay currentData={currentData} />
                </Segment>
                <Segment>
                    <Header as="h5">Log Data:</Header>
                    <div style={{ width: "100%" }}>
                        <div style={{ margin: "auto", width: "max-content" }}>
                            <Button content="Export Log" basic onClick={saveLog} />
                            <Button content="Clear Log" basic onClick={clearLog} />
                        </div>
                    </div>
                </Segment>
                <Segment>
                    <Header as="h5">Payload Release</Header>
                    <div style={{ width: "100%" }}>
                        <div style={{ margin: "auto", width: "max-content" }}>
                            <Button basic color='blue' content="Water Module" icon='tint' basic onClick={this.dropWater()} />
                            <Button basic color='yellow' content="Habitat Module" icon='home' basic onClick={this.dropHabitat()} />
                            <td>Payload Prediction</td>
                        </div>
                    </div>
                </Segment>
            </Segment>
        );
    }
}

export default DataArea;
