import React, { Component } from 'react';
import { Header, Checkbox, Segment, Button, Input, Popup } from "semantic-ui-react";

class MapSettings extends Component {
  state = {
    settingsOpen: true,
    showPopup: false,
    targLat: 49.258063,
    targLng: -123.195911,
    pTLat: 49.258063,
    pTLng: -123.195911,
  };

  toggleSettings = () => {
    this.setState((state) => { return { settingsOpen: !state.settingsOpen }; });
  };

  handleOpen = () => {
    this.setState({ showPopup: true });
  };

  handleClose = () => {
    this.setState({ showPopup: false });
  };

  passTarget = () => {
    const { pTLat, pTLng } = this.state;
    this.setState({ targLat: pTLat, targLng: pTLng });
    this.props.setNewTarget(pTLat, pTLng);
  };

  setTLAT = (event) => {
    if (!(event.target.value > 90 || event.target.value < -90)) {
      this.setState({ pTLat: event.target.value });
    }
  };

  setTLNG = (event) => {
    if (!(event.target.value > 180 || event.target.value < -180)) {
      this.setState({ pTLng: event.target.value });
    }
  };

  openMarkup = () => {
    const { changeSettingsState, drawPlane, drawTarget, drawCDAPredictionPoint, drawPayloadPredictionPoint, drawPlanePositionHistory, followPlane } = this.props;
    return (
      <div style={{ padding: "1em", minWidth: "250px", minHeight: "100%", maxHeight: "100%" }}>
        <Header as="h4">Map Settings:</Header>
        <Segment style={{ display: "flex", flexDirection: "column" }}>
          <Header as="h5">Display Settings:</Header>
          <Checkbox
            toggle
            label="Plane"
            checked={drawPlane}
            onChange={() => { changeSettingsState({ drawPlane: !drawPlane }); }}
          />
          <Checkbox
            toggle
            label="Target"
            checked={drawTarget}
            onChange={() => { changeSettingsState({ drawTarget: !drawTarget }); }}
          />
          <Checkbox
            toggle
            label="CDA Prediction Point"
            checked={drawCDAPredictionPoint}
            onChange={() => { changeSettingsState({ drawCDAPredictionPoint: !drawCDAPredictionPoint }); }}
          />
          <Checkbox
            toggle
            label="Payload Prediction Point"
            checked={drawPayloadPredictionPoint}
            onChange={() => { changeSettingsState({ drawPayloadPredictionPoint: !drawPayloadPredictionPoint }); }}
          />
          <Checkbox
            toggle
            label="Wind direction"
          />
          <Checkbox
            toggle
            label="Heading"
          />
        </Segment>
        <Segment style={{ display: "flex", flexDirection: "column" }}>
          <Header as="h5">Movement:</Header>
          <Button
            style={{ marginBottom: "0.5em" }}
            basic
            content="Zoom camera to plane"
            onClick={this.props.zoomToPlane}
            icon="plane"
          />
          <Button
            style={{ marginBottom: "0.5em" }}
            basic
            content="Zoom camera to target"
            onClick={this.props.zoomToTarget}
            icon="target"
            disabled={!followPlane}
          />
          <Checkbox
            toggle
            label="Follow Plane"
            checked={!followPlane}
            onChange={() => {
              changeSettingsState({ followPlane: !followPlane });
            }}
          />
          <Checkbox
            toggle
            label="Trace Plane Route"
            checked={drawPlanePositionHistory}
            onChange={() => { changeSettingsState({ drawPlanePositionHistory: !drawPlanePositionHistory }); }}
          />
          <Button
            style={{ marginBottom: "0.5em" }}
            basic
            content="Clear Traced Route"
            onClick={this.props.clearRoute}
            icon="delete"
          />
        </Segment>

        <Segment style={{ display: "flex", flexDirection: "column" }}>
          <Header as="h5">Target:</Header>
          <p>
            Lattitude:&nbsp;
            <strong>{this.state.targLat}</strong>
          </p>
          <p>
            Longitude:&nbsp;
            <strong>{this.state.targLng}</strong>
          </p>
          <React.Fragment>
            <Popup
              trigger={<Button icon="target" basic content="Input Target Location" />}
              on="click"
              position="bottom center"
              open={this.state.showPopup}
              onOpen={this.handleOpen}
              onClose={this.handleClose}
            >
              <Input
                size="mini"
                label={{ basic: true, content: '째N' }}
                labelPosition="right"
                placeholder="Lattitude..."
                type="number"
                value={this.state.pTLat}
                onChange={this.setTLAT}
                style={{ marginBottom: "0.5em", minWidth: "200px" }}
                fluid
              />
              <Input
                size="mini"
                label={{ basic: true, content: '째W' }}
                labelPosition="right"
                placeholder="Longitude..."
                type="number"
                value={this.state.pTLng}
                onChange={this.setTLNG}
                style={{ marginBottom: "0.5em" }}
                fluid
              />
              <Button
                size="mini"
                fluid
                basic
                icon="check"
                primary
                content="Set Location"
                onClick={() => {
                  this.passTarget();
                  this.handleClose();
                }}
              />
            </Popup>
          </React.Fragment>
        </Segment>

        <Button
          compact
          size="mini"
          icon="chevron left"
          content="Hide Settings"
          style={{
            position: "absolute",
            bottom: "5em",
          }}
          onClick={this.toggleSettings}
        />
      </div>
    );
  };

  render() {
    const { settingsOpen } = this.state;
    if (settingsOpen) return this.openMarkup();
    else {
      return (
        <div style={{ position: "absolute", right: "1em", bottom: "5em" }}>
          <Button
            compact
            size="mini"
            icon="chevron right"
            content="Show Settings"
            onClick={this.toggleSettings}
            style={{ width: "100px" }}
          />
        </div>
      );
    }
  }
}

export default MapSettings;
