import React, { Component } from 'react';
import { Header, Checkbox, Segment, Button } from "semantic-ui-react";

class MapSettings extends Component {

    state = {
        settingsOpen: false,    
    };

    toggleSettings = ()  => {
        this.setState((state) => { return {settingsOpen: !state.settingsOpen}; });
    };

    openMarkup = () => {
        const { changeSettingsState, drawPlane } = this.props;
        return (
            <div style={{ padding: "1em", minWidth: "250px", minHeight:"100%", maxHeight: "100%" }}>
                <Header as="h4">Map Settings:</Header>
                <Segment style={{ display: "flex", flexDirection: "column"}}>
                    <Header as="h5">Display Settings</Header>
                    <Checkbox
                        toggle
                        label="Plane"
                        checked={drawPlane}
                        onChange={() => {changeSettingsState({ drawPlane: !drawPlane })}}
                    />
                    <Checkbox
                        toggle
                        label="Target"
                    />
                    <Checkbox
                        toggle
                        label="Wind direction"
                    />
                    <Checkbox
                        toggle
                        label="Heading"
                    />
                    <Checkbox
                        toggle
                        label="Drop Prediction Point"
                    />
                </Segment>
                <Segment style={{ display: "flex", flexDirection: "column"}}>
                    <Header as="h5">Movement</Header>
                    <Button
                        style={{ marginBottom: "0.5em"}}
                        basic
                        content="Zoom camera to plane"
                        onClick={this.props.zoomToPlane}
                    />
                    <Checkbox
                        toggle
                        label="Follow plane"
                    />
                </Segment>

                <Button
                    compact
                    size="mini"
                    icon="chevron left"
                    content="Hide Settings"
                    style={{
                        position: "absolute",
                        bottom: "1em",
                    }}
                    onClick={this.toggleSettings}
                />
            </div>
        );
    };

    render(){
        if (this.state.settingsOpen)
            return this.openMarkup();
        else{
            return (
                <div style={{ position: "absolute", right: "1em", bottom: "1em" }}>
                    <Button
                        compact
                        size="mini"
                        icon="chevron right"
                        content="Show Settings"
                        onClick={this.toggleSettings}
                        style={{ width: "100px" }}
                    />
                </div>
            )
        }
    }
}

export default MapSettings;
