import React, { Component } from 'react';
import { ResizableBox } from 'react-resizable';
import { ReactBingmaps } from 'react-bingmaps';
import "../css/resize.css";


class AeroMap extends Component {
    followPlaneLive = () => {
        if (followPlane) {
            this.setState({ pTLat: event.target.value });
        } 
    }

    render(){
        let { mapHeight, planeLat, planeLong, mapLat, mapLong, targLat, targLng, zoom, drawPlane, drawTarget, followPlane } = this.props;
        const polygons = [];
        drawPlane && polygons.push({
            "center":[planeLat, planeLong],
            "radius": 0.0015,
            "points": 20,
            "option": {fillColor: "#DC143C", strokeThickness: 2}
        });
        drawTarget && polygons.push ({
            "center":[targLat, targLng],
            "radius": 0.0094697, // 50 feet = 0.0094697 miles
            "points": 80,
            "option": {
                strokeColor: "#ff0000",
                strokeThickness: 1,
                strokeDashArray: [1, 2]
            }
        });
        return (
            <div>
                <ResizableBox
                    width={600}
                    height={mapHeight}
                    axis="x"
                    handleSize={[20, 20]}
                    minConstraints={[100, mapHeight]}
                >
                    <div style={{ backgroundColor: "#999", width: "100%", height: "100%", overflow: "hidden" }}>
                        <ReactBingmaps
                            bingmapKey = "AszsKaHJ3M1aGYwWB85QjTHkpEs8wTow6nOYAzOMDxOCopJSupN6RLCPkbVR42jF"
                            disableStreetside={true}
                            center = {[mapLat, mapLong]}
                            zoom={zoom}
                            regularPolygons = {polygons}
                        />
                    </div>
                </ResizableBox>
            </div>
        );
    }
}

export default AeroMap;
