import React, { Component } from 'react';
import { Segment, Header, Icon, Button } from "semantic-ui-react";

// eslint-disable-next-line no-unused-vars
const localServer = "ws://localhost:8888";
const remoteServer = "ws://104.248.73.89:8888";// ?????
const server = remoteServer;

class CommunicationArea extends Component {
  constructor(props) {
    super(props);
    this.connect();
  }

    state = {
      connected: false,
    };

    receivedData = (data) => {
      console.info("received:", data);
      this.props.receiveData(data);
    };

    connect = () => {
      console.info("Connecting....");
      const connection = new WebSocket(server);
      connection.onopen = () => {
        this.setState({ connected: true });
        console.info("Connected to server");
      };
      connection.onerror = (error) => {
        console.info("Connection error", error);
      };
      connection.onmessage = (e) => {
        this.receivedData(e.data);
      };
      connection.onclose = () => {
        this.setState({ connected: false });
        console.info("Disconnected from server");
      };
      this.connection = connection;
    };

    sendData = () => {
      const { connected } = this.state;
      if (!connected) {
        console.warn("Cannot send. Not connected");
        return;
      }
      const data = JSON.stringify(
        {
          type: "test_data",
          flightData: {
            latitude: 49.258063+Math.random()*0.00111,
            longitude: -123.195911+Math.random()*0.00111,
            altitude: 45.3,
            velocity: 10.3,
            headwind: -0.9,
          },
        },
      );
      console.info("Sending:", data);
      this.connection.send(data);
    };

    render() {
      const { connected } = this.state;
      return (
        <Segment>
          <Header as="h5" style={{ marginBottom: "0px" }}>Communication:</Header>
          <div style={{ color: "grey" }}>
            Connection status:&nbsp;
            {connected ? (
              <strong style={{ color: "limegreen" }}>Connected</strong>
            ): (
              <span>
                <strong style={{ color: "firebrick" }}>Not Connected</strong>
                                &nbsp;
                <Icon link name="sync" onClick={this.connect} />
              </span>

            )}
            <Button icon="send" content="Send Test Data" fluid basic onClick={this.sendData} />
          </div>
        </Segment>
      );
    }
}

export default CommunicationArea;
