import React, { Component } from 'react';
import { Segment, Header, Button, Icon } from "semantic-ui-react";
import "./style.css";

class DropArea extends Component {
  constructor(props) {
    super(props);
    this.state = {
      waterLine: "",
      habitatLine: "",
      cdaOne: "",
      cdaTwo: "",
      altitude: null,
      dropOpen: true,
      cargoDropped: false,
      oneDropped: false,
      twoDropped: false,
    };
  }

    toggleSettings = () => {
      this.setState((state) => { return { dropOpen: !state.dropOpen }; });
    };

    // Font should be 0.5" == 36 pt

    dropPayload = () => {
      const { currentData } = this.props;
      if (!currentData || !currentData.flightData) {
        this.setState({ waterLine: "Error: Altitude not found!" });
        this.setState({ habitatLine: "Error: Altitude not found!" });
        return;
      }
      if (!this.state.cargoDropped) {
        // SEND SIGNAL THROUGH SERVER TO PHONE, PHONE SHOULD INTERPRET THIS INPUT AND TELL SERVO TO RELEASE WATER + HABITATS
        this.setState( { altitude: currentData.flightData.altitude});
        this.setState({ waterLine: `${this.state.dropLine}Water released at ${currentData.flightData.altitude} m.` });
        this.setState({ habitatLine: `${this.state.dropLine}Habitat released at ${currentData.flightData.altitude} m.` });
        this.setState({ cargoDropped: true });
      } else if (this.state.cargoDropped) {
        // you have already dropped the payload!
      }
    };


    releaseCDA = () => {
      const { currentData } = this.props;
      if (!currentData || !currentData.flightData) {
        this.setState({ cdaOne: "Error: Altitude not found!" });
        this.setState({ cdaTwo: "Error: Altitude not found!" });
        return;
      }
      if (!this.state.oneDropped) {
        // SEND SIGNAL THROUGH SERVER TO PHONE, PHONE SHOULD INTERPRET THIS INPUT AND TELL SERVO TO RELEASE WATER + HABITATS
        this.setState({ cdaOne: `${this.state.dropLine}CDA released at ${currentData.flightData.altitude} m.` });
        this.setState({ oneDropped: true });
      } else if (!this.state.twoDropped) {
        // SEND SIGNAL THROUGH SERVER TO PHONE, PHONE SHOULD INTERPRET THIS INPUT AND TELL SERVO TO RELEASE WATER + HABITATS
        this.setState({ cdaTwo: `${this.state.dropLine}CDA released at ${currentData.flightData.altitude} m.` });
        this.setState({ twoDropped: true });
      } else if (this.state.oneDropped && this.state.twoDropped) {
        // you have already dropped both CDAs!
      }
    };

    render() {
      const { currentData } = this.props;
      const { waterLine, habitatLine, cdaOne, cdaTwo } = this.state;
      return (
        <Segment
          style={{
            height: "100%",
            marginTop: "0px",
            marginLeft: "1em",
            minWidth: "300px",
            boxSizing: "border-box",
          }}
        >
          <Header as="h4">Payload Release:</Header>
          <Segment>
            <Header as="h5">Airdrop:</Header>
            <div style={{ width: "100%" }}>
              <div style={{ margin: "auto", width: "max-content" }}>
                <Button basic color="brown" content="Cargo (Modules)" icon="first aid" basic onClick={this.dropPayload} />
                <br />
                <br />
                <Button basic color="violet" content="Colonists (CDAs)" icon="space shuttle" basic onClick={this.releaseCDA} />
              </div>
            </div>
            {' '}
            <br />


            <div style={{ width: "100%", overflow: "hidden", fontSize: "12pt" }}>
              <table id="flight_data_table">
                <tbody>
                  <tr>
                    <td>
                      <Header>
                          <Icon name="tint" size="tiny" />
                            Water:
                          {waterLine}
                        </Header>
                    </td>
                  </tr>
                  <tr>
                    <td>
                      <Header>
                          <Icon name="home" size="tiny" />
                            Habitat:
                            {habitatLine}
                        </Header>
                    </td>
                  </tr>
                  <tr>
                    <td>
                      <Header>
                          <Icon name="users" size="tiny" />
                            CDA 1:
                          {cdaOne}
                        </Header>
                    </td>
                  </tr>
                  <tr>
                    <td>
                      <Header>
                          <Icon name="users" size="tiny" />
                            CDA 2:
                          {cdaTwo}
                        </Header>
                    </td>
                  </tr>
                </tbody>
              </table>
            </div>
          </Segment>
        </Segment>
      );
    }
}

export default DropArea;
