import React, { Component } from 'react';
import { Header, Checkbox, Segment, Button, Input, Popup, Message } from "semantic-ui-react";

class MapSettings extends Component {
    constructor(props) {
        super(props);
        this.state = {
            settingsOpen: false,
            showPopup: false,
            targLat: 49.258063,
            targLng: -123.195911,
        }
    }

    toggleSettings = () => {
        this.setState((state) => { return { settingsOpen: !state.settingsOpen }; });
    };

    handleOpen = () => {
        this.setState({ showPopup: true });
    };

    handleClose = () => {
        this.setState({ showPopup: false });
    };
    
    passTarget = () => {
        var passedLat = this.state.targLat;
        var passedLng = this.state.targLng;
        this.props.setNewTarget(passedLat, passedLng);
    }

    setTLAT = (event) => {
        if (!(90 < event.target.value || event.target.value < -90)) {
            this.setState({ targLat: event.target.value });
        } 
    }

    setTLNG = (event) => {
        if (!(180 < event.target.value || event.target.value < -180)) {
            this.setState({ targLng: event.target.value });
        }
    }

    openMarkup = () => {
        const { changeSettingsState, drawPlane, drawTarget } = this.props;
        return (
            <div style={{ padding: "1em", minWidth: "250px", minHeight: "100%", maxHeight: "100%" }}>
                <Header as="h4">Map Settings:</Header>
                <Segment style={{ display: "flex", flexDirection: "column" }}>
                    <Header as="h5">Display Settings</Header>
                    <Checkbox
                        toggle
                        label="Plane"
                        checked={drawPlane}
                        onChange={() => { changeSettingsState({ drawPlane: !drawPlane }) }}
                    />
                    <Checkbox
                        toggle
                        label="Target"
                        checked={drawTarget}
                        onChange={() => { changeSettingsState({ drawTarget: !drawTarget }) }}
                    />
                    <Checkbox
                        toggle
                        label="Wind direction"
                    />
                    <Checkbox
                        toggle
                        label="Heading"
                    />
                    <Checkbox
                        toggle
                        label="Drop Prediction Point"
                    />
                </Segment>
                <Segment style={{ display: "flex", flexDirection: "column"}}>
                    <Header as="h5">Movement</Header>
                    <Button
                        style={{ marginBottom: "0.5em"}}
                        basic
                        content="Zoom camera to plane"
                        onClick={this.props.zoomToPlane}
                    />
                    <Button
                        style={{ marginBottom: "0.5em"}}
                        basic
                        content="Zoom camera to target"
                        onClick={this.props.zoomToTarget}
                    />
                    <Checkbox
                        toggle
                        label="Follow plane"
                    />
                </Segment>

                <Segment style={{ display: "flex", flexDirection: "column" }}>
                    <Header as="h5">Target</Header>
                    <p>Lattitude:   {this.state.targLat}</p>
                    <p>Longitude:   {this.state.targLng}</p>
                    <React.Fragment>
                        <Popup
                            trigger={<Button color='olive'>Input Target Location</Button>}
                            on='click'
                            position='bottom center'
                            open={this.state.showPopup}
                            onOpen={this.handleOpen}
                        >
                            <label>
                                <Input
                                    size='mini'
                                    label={{ basic: true, content: '째N' }}
                                    labelPosition='right'
                                    placeholder='Lattitude...'
                                    type="number"
                                    value={this.state.targLat}
                                    onChange={this.setTLAT}
                                />
                                <Input
                                    size='mini'
                                    label={{ basic: true, content: '째W' }}
                                    labelPosition='right'
                                    placeholder='Longitude...'
                                    type="number"
                                    value={this.state.targLng}
                                    onChange={this.setTLNG}
                                />
                            </label>
                            <Button
                                size="mini"
                                content="Submit"
                                color='olive'
                                onClick={() => {
                                    this.passTarget;
                                    this.handleClose;
                                }}
                            />
                        </Popup>
                    </React.Fragment>
                </Segment>

                <Button
                    compact
                    size="mini"
                    icon="chevron left"
                    content="Hide Settings"
                    style={{
                        position: "absolute",
                        bottom: "4em",
                    }}
                    onClick={this.toggleSettings}
                />
            </div>
        );
    };

    render() {
        if (this.state.settingsOpen)
            return this.openMarkup();
        else {
            return (
                <div style={{ position: "absolute", right: "1em", bottom: "4em" }}>
                    <Button
                        compact
                        size="mini"
                        icon="chevron right"
                        content="Show Settings"
                        onClick={this.toggleSettings}
                        style={{ width: "100px" }}
                    />
                </div>
            )
        }

    }
}

export default MapSettings;
